clc;clear all;close all;file1=input('\nEnter input filename=','s');file2=input('\nEnter output filename=','s');zoom=input('\nEnter zoom factor=');x=imread(file1);[m,n,d]=size(x);r=1;a=uint8(zeros(m*zoom,n*zoom,d));for i=1:m    c=1;    for j=1:n       P(1:d)=x(i,j,1:d);%        a(r:r+n,c:c+n,1:d)=P(1:d,1,1);        for k=r:(r+zoom-1)           for l=c:(c+zoom-1)               a(k,l,1:d)=P(1:d);           end       end       c=c+zoom;    end    r=r+zoom;endimshow(a);imwrite(a,file2);